import api from '../api/api';

import { GET_REPOSITORIES } from './repositories.conts';


export const getItem = () => {
    try {
        const response = api('https://api.github.com/repositories');

        response.then(() => {
            if (response) {
                return {
                    type: GET_REPOSITORIES,
                    payload: response
                };
            }
        })

    } catch (error) {
        console.error("getItem -> error", error)
    }

};
