import api from '../api/api';

import { GET_REPOSITORIES } from './repositories.conts';


export async function getItem(dispatch, getState) {
    const response = await api('https://api.github.com/repositories')
    dispatch({ type: GET_REPOSITORIES, payload: response })
}

