import fetch from 'isomorphic-unfetch';
import type http from 'http';
// import { IS_WEB, IS_REACT_NATIVE } from './environment.const';
import { ApiError, ValidationError, PermissionError } from '../error/error.types';

// constants
// import env from '../../server/env';
// import { defaultLanguage } from '../app/languages';
// import { mobileHeaders } from './api';

import type { Response, Options } from './api.types';

export function isValid<T>(url: string, response: Response<T>): Response<T> | never {
    if (response.code === 401) {
        throw new PermissionError(`Access denied [${response.code}] to ${url}`);
    }

    if (response.status !== 'ok') {
        throw new ValidationError(`${response.code} ${response.data}`, {
            code: response.code,
            data: response.data,
        });
    }
    return response;
}

// `${env.api}${url}${url.includes('?') ? `&language=${lang}` : `?language=${lang}`}`
export default async function api<T>(
    url: string,
    lang?: string,
    options?: Options,
    headers?: http.OutgoingHttpHeaders,
) {
    // const credentials: RequestCredentials = 'omit';
    const mode: RequestMode = 'cors';
    try {
        const request = fetch(
            `${url}`,
            Object.assign(
                {},
                {
                    headers: {
                        Accept: 'application/json',
                        'Content-Type': 'application/json',
                        // 'Accept-Language': lang || defaultLanguage,
                        // 'Content-Language': lang || defaultLanguage,
                        ...headers,
                    },
                    mode,
                    // credentials,
                },
                options,
                options?.body && options?.method !== 'GET' ? { body: JSON.stringify(options.body) } : {},
            ),
        );
        const result = await request;
        // const res = result.body;
        // console.log("res", res)

        // if (result.ok) {
        //     console.log("result", result.json())
        // }

        let response: Response<T> = null!;

        if (result.headers.get('content-type') === 'application/json') {
            response = await result.json();
            console.log("result", result)
        } else {
            throw new ValidationError('Json parsing error', {
                code: result.status,
                data: {
                    url,
                    method: options?.method,
                },
            });
        }

        // if (process.env.NODE_ENV === 'development') {
        //     /* eslint-disable no-console */
        //     console.group(`API Request Debug ${url}`);
        //     console.info(`Url ${url}`);
        //     console.info('Response', response);
        //     console.groupCollapsed('Stack trace');
        //     console.trace();
        //     console.groupEnd();
        //     console.groupEnd();
        //     /* eslint-enable no-console */
        // }

        // return [result, result.headers];
        // return [isValid(url, response), result.headers];
    } catch (e) {
        if (e instanceof ValidationError || e instanceof PermissionError) {
            throw e;
        }

        throw new ApiError(e.message, {
            code: 500,
            stack: e.stack,
        });
    }
}

export const pause = (duration: number): Promise<void> =>
    new Promise(res => setTimeout(res, duration));


export async function retry<T>(
    retries: number,
    fn: () => Promise<T>,
    delay = 500,
    delayMultiply = 1,
): Promise<T> {
    try {
        return await fn();
    } catch (e) {
        if (retries > 1) {
            await pause(delay);
            return retry(retries - 1, fn, delay * delayMultiply);
        }
        throw e;
    }
}
